// Prepare Sora API request
const originalPrompt = $('ğŸ¯ Webhook: Video Request').item.json.body.prompt;
const enhancedPrompt = $input.item.json.text || $input.item.json.message?.content || originalPrompt;
const useEnhanced = $('ğŸ¯ Webhook: Video Request').item.json.body.use_ai_enhance !== false;
const finalPrompt = useEnhanced ? enhancedPrompt : originalPrompt;

// Get optional parameters from request
const requestBody = $('ğŸ¯ Webhook: Video Request').item.json.body;

return {
  json: {
    original_prompt: originalPrompt,
    enhanced_prompt: enhancedPrompt,
    final_prompt: finalPrompt.replace(/[\n\r]/g, ' ').trim(),
    use_ai_enhance: useEnhanced,
    model: requestBody.model || 'sora-2-text-to-video',
    aspect_ratio: requestBody.aspect_ratio || '16:9',
    duration: requestBody.duration || 5,
    callback_url: requestBody.callback_url || null,
    folder_id: requestBody.folder_id || 'root',
    video_name: requestBody.video_name || `sora_video_${Date.now()}`
  }
};

// Extract task ID from Sora response
const response = $input.item.json;
const prevData = $('ğŸ“ Prepare Sora Request').item.json;

if (response.code !== 0 || !response.data?.task_id) {
  throw new Error(`Sora API Error: ${response.message || 'Unknown error'}`);
}

return {
  json: {
    task_id: response.data.task_id,
    status: 'processing',
    poll_count: 0,
    max_polls: 60,
    poll_interval: 5,
    original_prompt: prevData.original_prompt,
    enhanced_prompt: prevData.enhanced_prompt,
    final_prompt: prevData.final_prompt,
    model: prevData.model,
    aspect_ratio: prevData.aspect_ratio,
    duration: prevData.duration,
    callback_url: prevData.callback_url,
    folder_id: prevData.folder_id,
    video_name: prevData.video_name
  }
};

// Evaluate task status
const statusResponse = $input.item.json;
const prevData = $('â³ Wait 5s').item.json;

const pollCount = (prevData.poll_count || 0) + 1;
const status = statusResponse.data?.status || 'unknown';
const videoUrl = statusResponse.data?.result_url || statusResponse.data?.video_url || null;

// Check if completed
const isCompleted = status === 'completed' || status === 'success' || videoUrl;
const isFailed = status === 'failed' || status === 'error';
const isTimeout = pollCount >= prevData.max_polls;

return {
  json: {
    ...prevData,
    poll_count: pollCount,
    current_status: status,
    video_url: videoUrl,
    is_completed: isCompleted,
    is_failed: isFailed,
    is_timeout: isTimeout,
    should_continue: !isCompleted && !isFailed && !isTimeout,
    raw_response: statusResponse
  }
};

// Prepare final success response
const uploadResult = $input.item.json;
const statusData = $('ğŸ“Š Evaluate Status').item.json;

const driveFileId = uploadResult.id;
const driveViewLink = `https://drive.google.com/file/d/${driveFileId}/view`;
const driveDownloadLink = `https://drive.google.com/uc?export=download&id=${driveFileId}`;

return {
  json: {
    success: true,
    message: 'Video Ä‘Ã£ Ä‘Æ°á»£c táº¡o vÃ  upload thÃ nh cÃ´ng!',
    data: {
      task_id: statusData.task_id,
      video_url: statusData.video_url,
      google_drive: {
        file_id: driveFileId,
        file_name: uploadResult.name,
        view_link: driveViewLink,
        download_link: driveDownloadLink
      },
      prompt: {
        original: statusData.original_prompt,
        enhanced: statusData.enhanced_prompt,
        used: statusData.final_prompt
      },
      settings: {
        model: statusData.model,
        aspect_ratio: statusData.aspect_ratio,
        duration: statusData.duration
      },
      processing: {
        poll_count: statusData.poll_count,
        total_time_seconds: statusData.poll_count * 5
      }
    },
    timestamp: new Date().toISOString()
  }
};

// Prepare failure response
const statusData = $input.item.json;

let errorMessage = 'Video generation failed';
if (statusData.is_timeout) {
  errorMessage = `Timeout sau ${statusData.poll_count * 5} giÃ¢y. Video cÃ³ thá»ƒ váº«n Ä‘ang xá»­ lÃ½.`;
} else if (statusData.is_failed) {
  errorMessage = 'Sora API tráº£ vá» lá»—i khi táº¡o video';
}

return {
  json: {
    success: false,
    error: errorMessage,
    data: {
      task_id: statusData.task_id,
      status: statusData.current_status,
      poll_count: statusData.poll_count,
      prompt: statusData.original_prompt
    },
    timestamp: new Date().toISOString()
  }
};

// Loop back to wait node with current state
const currentData = $input.item.json;

return {
  json: {
    task_id: currentData.task_id,
    status: currentData.current_status,
    poll_count: currentData.poll_count,
    max_polls: currentData.max_polls,
    poll_interval: currentData.poll_interval,
    original_prompt: currentData.original_prompt,
    enhanced_prompt: currentData.enhanced_prompt,
    final_prompt: currentData.final_prompt,
    model: currentData.model,
    aspect_ratio: currentData.aspect_ratio,
    duration: currentData.duration,
    callback_url: currentData.callback_url,
    folder_id: currentData.folder_id,
    video_name: currentData.video_name
  }
};
