// Generate activation token (simple example)
const crypto = require('crypto');
const token = crypto.randomBytes(32).toString('hex');

// Prepare user data
const userData = {
  name: $input.item.json.name,
  email: $input.item.json.email,
  activation_token: token,
  created_at: new Date().toISOString()
};

// Build activation link
const activationLink = `https://longsang.org/activate?token=${token}`;

// Prepare email variables
const emailVars = {
  user_name: userData.name,
  company_name: "Long Sang Portfolio",
  activation_link: activationLink,
  feature_1: "Truy cập dashboard cá nhân",
  feature_2: "Kết nối với cộng đồng",
  feature_3: "Nhận ưu đãi độc quyền",
  support_email: "support@longsang.org",
  company_address: "Ho Chi Minh City, Vietnam",
  website_url: "https://longsang.org",
  unsubscribe_link: `https://longsang.org/unsubscribe?email=${userData.email}`
};

return {
  json: {
    user_data: userData,
    email_vars: emailVars
  }
};

// Load welcome email template
const fs = require('fs');
const templatePath = 'D:/0.PROJECTS/00-MASTER-ADMIN/longsang-admin/templates/emails/welcome-email.html';
let template = fs.readFileSync(templatePath, 'utf8');

// Get variables
const vars = $input.item.json.email_vars;

// Replace variables
Object.keys(vars).forEach(key => {
  const regex = new RegExp(`{{${key}}}`, 'g');
  template = template.replace(regex, vars[key]);
});

return {
  json: {
    html: template,
    to: $input.item.json.user_data.email,
    subject: `Chào mừng đến với Long Sang, ${$input.item.json.user_data.name}!`,
    user_data: $input.item.json.user_data
  }
};
